PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX dcterms: <http://purl.org/dc/terms/>
PREFIX genex: <http://purl.org/genex#>
PREFIX lscr: <http://purl.org/lscr#>
PREFIX orth: <http://purl.org/net/orth#>
PREFIX up: <http://purl.uniprot.org/core/>
PREFIX sio: <http://semanticscience.org/resource/>
SELECT distinct ?gene_id ?orthologousTo ?uniprot ?annotation_text WHERE {
	SERVICE <https://bgee.org/sparql/> {
		SELECT ?gene ?gene_id {
			?gene a orth:Gene .
			?gene genex:isExpressedIn ?anat .
			?gene dcterms:identifier ?gene_id .
			?anat rdfs:label '$$brain$$' .
			?gene orth:organism ?o .
			?o obo:RO_0002162 ?taxon .
			?taxon up:commonName "$$fruitfly$$" .
		}
		$$innerlimit$$
	}

	SERVICE <https://sparql.omabrowser.org/sparql/> {
		SELECT ?uniprot ?gene ?orthologousTo {
			?cluster a orth:OrthologsCluster .
			?cluster orth:hasHomologousMember ?node1 .
			?cluster orth:hasHomologousMember ?node2 .
			?node2 orth:hasHomologousMember* ?orthologousTo .
			?node1 orth:hasHomologousMember* ?protein1 .
			?protein1 sio:SIO_010079 ?gene .
			?orthologousTo lscr:xrefUniprot ?uniprot .
			filter(?node1 != ?node2)
		}
	}

	SERVICE <http://sparql.uniprot.org/sparql>{
		?uniprot up:annotation ?annotation .
		?annotation rdfs:comment ?annotation_text .
	}
}

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX dcterms: <http://purl.org/dc/terms/>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX genex: <http://purl.org/genex#>
PREFIX lscr: <http://purl.org/lscr#>
PREFIX orth: <http://purl.org/net/orth#>
PREFIX up: <http://purl.uniprot.org/core/>
PREFIX sio: <http://semanticscience.org/resource/>
SELECT ?gene ?OMA_LINK ?uniprot ?annotation_text WHERE {
	SELECT * {
		SERVICE <https://bgee.org/sparql/> {
			SELECT ?gene {
				?gene genex:isExpressedIn ?anat .
				?anat rdfs:label '$$brain$$' .
				?gene orth:organism ?o .
				?o obo:RO_0002162 ?taxon .
				?taxon up:commonName '$$fruitfly$$' .
			}
			$$innerlimit$$
		}
		SERVICE <https://sparql.omabrowser.org/sparql/> {
			SELECT ?gene ?OMA_LINK ?uniprot{
				?protein2 a orth:Protein .
				?protein1 a orth:Protein .
				?clusterPrimates a orth:OrthologsCluster .
				?cluster a orth:OrthologsCluster .
				?cluster orth:hasHomologousMember ?node1 .
				?cluster orth:hasHomologousMember ?node2 .
				?node2 orth:hasHomologousMember* ?clusterPrimates .
				?clusterPrimates orth:hasHomologousMember* ?protein2 .
				?node1 orth:hasHomologousMember* ?protein1 .
				?protein1 sio:SIO_010079 ?gene. #encoded by
				?protein2 rdfs:seeAlso ?OMA_LINK .
				?clusterPrimates orth:hasTaxonomicRange ?taxRange .
				?taxRange orth:taxRange ?taxLevel .
				?protein2 lscr:xrefUniprot ?uniprot .
				VALUES ?taxLevel { '$$primates$$' }
				FILTER ( ?node1 != ?node2 )
			}
		}
		SERVICE <https://sparql.uniprot.org/sparql> {
			SELECT ?annotation_text ?uniprot {
				?uniprot up:annotation ?annotation .
				?annotation rdfs:comment ?annotation_text .
			}
		}
	}
}

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX orth: <http://purl.org/net/orth#>
PREFIX sio: <http://semanticscience.org/resource/>
PREFIX taxon: <http://purl.uniprot.org/taxonomy/>
PREFIX up: <http://purl.uniprot.org/core/>
PREFIX lscr: <http://purl.org/lscr#>
PREFIX genex: <http://purl.org/genex#>
SELECT DISTINCT ?protein ?orthologous_protein ?gene ?annotation_text WHERE {
	SELECT * {
		SERVICE <http://sparql.uniprot.org/sparql> {
			SELECT ?protein ?annotation_text WHERE {
				?protein a up:Protein ;
				up:organism taxon:9606 ;
				up:annotation ?annotation .
				?annotation rdfs:comment ?annotation_text .
				?annotation a up:Disease_Annotation .
				FILTER CONTAINS (?annotation_text, "$$glioblastoma$$")
			}
		}
		SERVICE <https://sparql.omabrowser.org/sparql/> {
			SELECT ?orthologous_protein ?protein ?gene WHERE {
				?protein_OMA a orth:Protein .
				?orthologous_protein a orth:Protein .
				?cluster a orth:OrthologsCluster .
				?cluster orth:hasHomologousMember ?node1 .
				?cluster
				orth:hasHomologousMember ?node2 .
				?node2 orth:hasHomologousMember* ?protein_OMA .
				?node1 orth:hasHomologousMember* ?orthologous_protein .
				?orthologous_protein orth:organism/obo:RO_0002162 taxon:10116 . # rattus norvegicus
				?orthologous_protein sio:SIO_010079 ?gene .
				?protein_OMA lscr:xrefUniprot ?protein .
				FILTER(?node1 != ?node2)
			}
		}
		SERVICE <https://bgee.org/sparql/> {
			 ?gene genex:isExpressedIn ?a .
			?a rdfs:label "$$brain$$" .
			?gene orth:organism ?s . 
			?s obo:RO_0002162 taxon:10116.
		}
	}
}

