questions.SPARQL:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX dcterms: <http://purl.org/dc/terms/>
PREFIX genex: <http://purl.org/genex#>
PREFIX lscr: <http://purl.org/lscr#>
PREFIX orth: <http://purl.org/net/orth#>
PREFIX up: <http://purl.uniprot.org/core/>
PREFIX sio: <http://semanticscience.org/resource/>
SELECT distinct ?gene_id ?orthologousTo ?uniprot ?annotation_text WHERE {
	SERVICE <https://www.bgee.org/sparql/> {
		SELECT ?gene ?gene_id {
			?gene a orth:Gene .
			?gene genex:isExpressedIn ?anat .
			?gene dcterms:identifier ?gene_id .
			?anat rdfs:label '$$brain$$' .
			?gene orth:organism ?o .
			?o obo:RO_0002162 ?taxon .
			?taxon up:commonName "$$fruitfly$$" .
		}
		$$innerlimit$$
	}

	SERVICE <https://sparql.omabrowser.org/sparql/> {
		SELECT ?uniprot ?gene ?orthologousTo {
			?cluster a orth:OrthologsCluster .
			?cluster orth:hasHomologousMember ?node1 .
			?cluster orth:hasHomologousMember ?node2 .
			?node2 orth:hasHomologousMember* ?orthologousTo .
			?node1 orth:hasHomologousMember* ?protein1 .
			?protein1 sio:SIO_010079 ?gene .
			?orthologousTo lscr:xrefUniprot ?uniprot .
			filter(?node1 != ?node2)
		}
	}

	SERVICE <http://sparql.uniprot.org/sparql>{
		?uniprot up:annotation ?annotation .
		?annotation rdfs:comment ?annotation_text .
	}
}

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX dcterms: <http://purl.org/dc/terms/>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX genex: <http://purl.org/genex#>
PREFIX lscr: <http://purl.org/lscr#>
PREFIX orth: <http://purl.org/net/orth#>
PREFIX up: <http://purl.uniprot.org/core/>
PREFIX sio: <http://semanticscience.org/resource/>
SELECT ?gene ?OMA_LINK ?uniprot ?annotation_text WHERE {
	SELECT * {
		SERVICE <https://www.bgee.org/sparql/> {
			SELECT ?gene {
				?gene genex:isExpressedIn ?anat .
				?anat rdfs:label '$$brain$$' .
				?gene orth:organism ?o .
				?o obo:RO_0002162 ?taxon .
				?taxon up:commonName '$$fruitfly$$' .
			}
			$$innerlimit$$
		}
		SERVICE <https://sparql.omabrowser.org/sparql/> {
			SELECT ?gene ?OMA_LINK ?uniprot{
				?protein2 a orth:Protein .
				?protein1 a orth:Protein .
				?clusterPrimates a orth:OrthologsCluster .
				?cluster a orth:OrthologsCluster .
				?cluster orth:hasHomologousMember ?node1 .
				?cluster orth:hasHomologousMember ?node2 .
				?node2 orth:hasHomologousMember* ?clusterPrimates .
				?clusterPrimates orth:hasHomologousMember* ?protein2 .
				?node1 orth:hasHomologousMember* ?protein1 .
				?protein1 sio:SIO_010079 ?gene. #encoded by
				?protein2 rdfs:seeAlso ?OMA_LINK .
				?clusterPrimates orth:hasTaxonomicRange ?taxRange .
				?taxRange orth:taxRange ?taxLevel .
				?protein2 lscr:xrefUniprot ?uniprot .
				VALUES ?taxLevel { '$$primates$$' }
				FILTER ( ?node1 != ?node2 )
			}
		}
		SERVICE <https://sparql.uniprot.org/sparql> {
			SELECT ?annotation_text ?uniprot {
				?uniprot up:annotation ?annotation .
				?annotation rdfs:comment ?annotation_text .
			}
		}
	}
}

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX orth: <http://purl.org/net/orth#>
PREFIX sio: <http://semanticscience.org/resource/>
PREFIX taxon: <http://purl.uniprot.org/taxonomy/>
PREFIX up: <http://purl.uniprot.org/core/>
PREFIX lscr: <http://purl.org/lscr#>
PREFIX genex: <http://purl.org/genex#>
SELECT DISTINCT ?protein ?orthologous_protein ?gene ?annotation_text WHERE {
	SELECT * {
		SERVICE <http://sparql.uniprot.org/sparql> {
			SELECT ?protein ?annotation_text WHERE {
				?protein a up:Protein ;
				up:organism taxon:9606 ;
				up:annotation ?annotation .
				?annotation rdfs:comment ?annotation_text .
				?annotation a up:Disease_Annotation .
				FILTER CONTAINS (?annotation_text, "$$glioblastoma$$")
			}
		}
		SERVICE <https://sparql.omabrowser.org/sparql/> {
			SELECT ?orthologous_protein ?protein ?gene WHERE {
				?protein_OMA a orth:Protein .
				?orthologous_protein a orth:Protein .
				?cluster a orth:OrthologsCluster .
				?cluster orth:hasHomologousMember ?node1 .
				?cluster
				orth:hasHomologousMember ?node2 .
				?node2 orth:hasHomologousMember* ?protein_OMA .
				?node1 orth:hasHomologousMember* ?orthologous_protein .
				?orthologous_protein orth:organism/obo:RO_0002162 taxon:10116 . # rattus norvegicus
				?orthologous_protein sio:SIO_010079 ?gene .
				?protein_OMA lscr:xrefUniprot ?protein .
				FILTER(?node1 != ?node2)
			}
		}
		SERVICE <https://www.bgee.org/sparql/> {
			 ?gene genex:isExpressedIn ?a .
			?a rdfs:label "$$brain$$" .
			?gene orth:organism ?s . 
			?s obo:RO_0002162 taxon:10116.
		}
	}
}

PREFIX up:<http://purl.uniprot.org/core/>
PREFIX taxon:<http://purl.uniprot.org/taxonomy/>
PREFIX rdfs:<http://www.w3.org/2000/01/rdf-schema#>
PREFIX orth:<http://purl.org/net/orth#>
PREFIX dcterms:<http://purl.org/dc/terms/>
PREFIX obo:<http://purl.obolibrary.org/obo/>
PREFIX lscr:<http://purl.org/lscr#>
PREFIX genex:<http://purl.org/genex#>
PREFIX sio: <http://semanticscience.org/resource/>

SELECT ?gene ?orthologous_protein2 WHERE {
	SELECT * {
		SERVICE <http://sparql.uniprot.org/sparql> {
			SELECT ?protein1 WHERE {
			?protein1 a up:Protein;
				up:organism/up:scientificName 'Homo sapiens' ;
				up:annotation ?annotation .
				?annotation rdfs:comment ?annotation_text.
				?annotation a up:Disease_Annotation .
				FILTER CONTAINS (?annotation_text, "$$cancer$$")
			}
		}

		SERVICE <https://sparql.omabrowser.org/sparql/> {
			SELECT ?orthologous_protein2 ?protein1 ?gene WHERE {
				?protein_OMA a orth:Protein .
				?orthologous_protein2 a orth:Protein .
				?cluster a orth:OrthologsCluster .
				?cluster orth:hasHomologousMember ?node1 .
				?cluster orth:hasHomologousMember ?node2 .
				?node2 orth:hasHomologousMember* ?protein_OMA .
				?node1 orth:hasHomologousMember* ?orthologous_protein2 
				.?orthologous_protein2 orth:organism/obo:RO_0002162/up:scientificName '$$rat$$' .
				?orthologous_protein2 sio:SIO_010079 ?gene .
				?protein_OMA lscr:xrefUniprot ?protein1 .
				FILTER(?node1 != ?node2)
			}
		}

		SERVICE <https://www.bgee.org/sparql/> {
			?gene genex:isExpressedIn ?anatEntity .
			?anatEntity rdfs:label '$$brain$$' .
			?gene orth:organism ?org . 
			?org obo:RO_0002162 taxon:10116 .
		}
	}
}

PREFIX genex: <http://purl.org/genex#>
PREFIX lscr: <http://purl.org/lscr#>
PREFIX orth: <http://purl.org/net/orth#>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX up: <http://purl.uniprot.org/core/>
SELECT DISTINCT ?anat_label ?GO_CLASS ?GO_LABEL {
	SERVICE <https://www.bgee.org/sparql/> {
		SELECT ?geneEns ?anat_label {
			?geneB a orth:Gene .
			?geneB genex:isExpressedIn ?anat .
			?geneB rdfs:label ?anatLabel ;
				lscr:xrefEnsemblGene ?geneEns .
			FILTER (UCASE($anatLabel) = UCASE('$$ins$$'))
			?anat rdfs:label ?anat_label .
			?geneB orth:organism ?o .
			?o obo:RO_0002162 ?taxon2 .
			?taxon2 up:commonName '$$zebrafish$$' .
		}
	}
	SERVICE <http://sparql.uniprot.org/sparql> {
		?uniprot rdfs:seeAlso / up:transcribedFrom ?geneEns .
		?uniprot a up:Protein .
		?uniprot up:classifiedWith ?GO_CLASS .
		?GO_CLASS rdfs:label ?GO_LABEL .
	}
}
$$innerlimit$$

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX up: <http://purl.uniprot.org/core/>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX genex: <http://purl.org/genex#>
PREFIX lscr: <http://purl.org/lscr#>
PREFIX orth: <http://purl.org/net/orth#>
SELECT DISTINCT ?geneEns ?uniprot ?annotation_text {
	SERVICE <https://www.bgee.org/sparql/> {
		SELECT ?geneEns {
			?geneB a orth:Gene .
			?geneB genex:isExpressedIn ?cond .
			?cond genex:hasAnatomicalEntity ?anat .
				?geneB lscr:xrefEnsemblGene ?geneEns .
		?anat rdfs:label '$$pancreas$$' .
		?geneB orth:organism ?o .
			?o obo:RO_0002162 ?taxon2 .
			?taxon2 up:commonName '$$human$$' .
		}
		$$innerlimit$$
	}
	SERVICE <http://sparql.uniprot.org/sparql> {
		?uniprot rdfs:seeAlso / up:transcribedFrom ?geneEns .
		?uniprot up:annotation ?annotation .
		?annotation rdfs:comment ?annotation_text .
	}
}

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX up: <http://purl.uniprot.org/core/>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX genex: <http://purl.org/genex#>
PREFIX lscr: <http://purl.org/lscr#>
PREFIX orth: <http://purl.org/net/orth#>
SELECT DISTINCT ?geneEns ?uniprot ?annotation_text {
	SERVICE <https://www.bgee.org/sparql/> {
		SELECT ?geneEns {
			?geneB genex:isExpressedIn ?cond ;
				lscr:xrefEnsemblGene ?geneEns .
			?cond genex:hasDevelopmentalStage ?st .
			?cond genex:hasAnatomicalEntity ?anat .
			?st rdfs:label '$$infantstage$$' .
			?anat rdfs:label '$$brain$$' .
			?geneB orth:organism ?o .
			?o obo:RO_0002162 ?taxon2 .
			?taxon2 up:commonName '$$human$$' .
		}
		$$innerlimit$$
	}
	SERVICE <http://sparql.uniprot.org/sparql> {
		?uniprot rdfs:seeAlso / up:transcribedFrom ?geneEns .
		?uniprot up:annotation ?annotation .
		?annotation a up:Disease_Annotation .
		?annotation rdfs:comment ?annotation_text .
	}
}

PREFIX lscr: <http://purl.org/lscr#>
PREFIX orth: <http://purl.org/net/orth#>
PREFIX up: <http://purl.uniprot.org/core/>
PREFIX obo: <http://purl.obolibrary.org/obo/>
SELECT DISTINCT ?proteinOMA ?species ?mnemonic ?evidenceType ?UniProt_URI WHERE {
	SERVICE <https://sparql.omabrowser.org/sparql/> {
		?proteinOMA a orth:Protein ;
			orth:organism/obo:RO_0002162/up:scientificName ?species ;
			rdfs:label '$$INS$$' .
		?proteinOMA lscr:xrefUniprot ?UniProt_URI .
	}
	SERVICE <http://sparql.uniprot.org/sparql> {
		?UniProt_URI up:mnemonic ?mnemonic ;
			up:existence/rdfs:label ?evidenceType .
	}
}

PREFIX lscr: <http://purl.org/lscr#>
PREFIX orth: <http://purl.org/net/orth#>
PREFIX up: <http://purl.uniprot.org/core/>
PREFIX obo: <http://purl.obolibrary.org/obo/>
SELECT DISTINCT ?PROTEIN_1 ?PROTEIN_2 ?UNIPROT_XREF_1 ?UNIPROT_XREF_2 WHERE {
	SERVICE <http://sparql.uniprot.org/sparql> {
		?taxon_1 up:commonName '$$mouse$$' .
		?taxon_2 up:commonName '$$rabbit$$' .
	}
	SERVICE <https://sparql.omabrowser.org/sparql/> {
		?cluster a orth:OrthologsCluster .
		?cluster orth:hasHomologousMember ?node1 .
		?cluster orth:hasHomologousMember ?node2 .
		?node2 orth:hasHomologousMember* ?PROTEIN_2 .
		?node1 orth:hasHomologousMember* ?PROTEIN_1 .
		?PROTEIN_1 a orth:Protein .
		?PROTEIN_1 orth:organism/obo:RO_0002162 ?taxon_1 ;
			rdfs:label '$$HBBY$$' ;
			lscr:xrefUniprot ?UNIPROT_XREF_1 .
		?PROTEIN_2 a orth:Protein .
		?PROTEIN_2 orth:organism/obo:RO_0002162 ?taxon_2 .
		?PROTEIN_2 lscr:xrefUniprot ?UNIPROT_XREF_2 .
		FILTER ( ?node1 != ?node2 )
	}
}

PREFIX up: <http://purl.uniprot.org/core/>
PREFIX orth: <http://purl.org/net/orth#>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX lscr: <http://purl.org/lscr#>
SELECT DISTINCT ?PROTEIN ?IS_PARALOGOUS_TO_PROTEIN ?UNIPROT_XREF ?PARALOG_UNIPROT_XREF ?annotation_text WHERE {
	SERVICE <https://sparql.omabrowser.org/sparql/> {
		?cluster a orth:ParalogsCluster .
		?cluster orth:hasHomologousMember ?node1 .
		?cluster orth:hasHomologousMember ?node2 .
		?node2 orth:hasHomologousMember* ?PROTEIN .
		?node1 orth:hasHomologousMember* ?IS_PARALOGOUS_TO_PROTEIN .
		?PROTEIN a orth:Protein .
		?PROTEIN orth:organism/obo:RO_0002162/up:scientificName '$$rattusnorvegicus$$' ;
			rdfs:label '$$Tp53$$' ;
			lscr:xrefUniprot ?UNIPROT_XREF .
		?IS_PARALOGOUS_TO_PROTEIN a orth:Protein .
		?IS_PARALOGOUS_TO_PROTEIN orth:organism/obo:RO_0002162/up:scientificName '$$rattusnorvegicus$$' .
		?IS_PARALOGOUS_TO_PROTEIN lscr:xrefUniprot ?PARALOG_UNIPROT_XREF .
	}
	SERVICE <http://sparql.uniprot.org/sparql> {
		?PARALOG_UNIPROT_XREF up:annotation ?annotation .
		?annotation a up:Function_Annotation .
		?annotation rdfs:comment ?annotation_text .
	}
	FILTER ( ?node1 != ?node2 )
}

PREFIX up: <http://purl.uniprot.org/core/>
PREFIX genex: <http://purl.org/genex#>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX orth: <http://purl.org/net/orth#>
PREFIX sio: <http://semanticscience.org/resource/>
PREFIX lscr: <http://purl.org/lscr#>
SELECT ?name1 ?protein1 ?name2 ?protein2 ?OMA_link2 ?anatomicalEntity {
	SELECT DISTINCT * {
		SERVICE <https://www.bgee.org/sparql/> {
			?taxon up:commonName '$$human$$' ;
				up:commonName ?name1 .
			?taxon2 up:commonName '$$mouse$$' ;
				up:commonName ?name2 .
		}
		SERVICE <https://sparql.omabrowser.org/sparql/> {
			?cluster a orth:OrthologsCluster .
			?cluster orth:hasHomologousMember ?node1 .
			?cluster orth:hasHomologousMember ?node2 .
			?node2 orth:hasHomologousMember* ?protein2 .
			?node1 orth:hasHomologousMember* ?protein1 .
			?protein1 a orth:Protein .
			?protein1 rdfs:label '$$INS$$' ;
				orth:organism/obo:RO_0002162 ?taxon .
			?protein2 a orth:Protein ;
				sio:SIO_010079 ?gene ; #is encoded by
				orth:organism/obo:RO_0002162 ?taxon2 .
			?gene lscr:xrefEnsemblGene ?geneEns .
			?protein2 rdfs:seeAlso ?OMA_link2 .
			FILTER ( ?node1 != ?node2 )
		}
		SERVICE <https://www.bgee.org/sparql/> {
			?geneB a orth:Gene .
				?geneB genex:isExpressedIn ?cond .
				?cond genex:hasAnatomicalEntity ?anat .
				?geneB lscr:xrefEnsemblGene ?geneEns .
			?anat rdfs:label '$$liver$$' ;
				rdfs:label ?anatomicalEntity .
			?geneB orth:organism ?o .
			?o obo:RO_0002162 ?taxon2 .
		}
	}
	$$innerlimit$$
}
$$innerlimit$$

PREFIX up: <http://purl.uniprot.org/core/>
PREFIX genex: <http://purl.org/genex#>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX orth: <http://purl.org/net/orth#>
PREFIX dcterms: <http://purl.org/dc/terms/>
SELECT DISTINCT ?id ?OMA_LINK WHERE {
	SELECT * {
		SERVICE <https://www.bgee.org/sparql/> {
			SELECT DISTINCT ?gene ?id {
				?gene a orth:Gene .
				?gene genex:isExpressedIn ?anat .
				?anat rdfs:label '$$brain$$' .
				?gene orth:organism ?o .
				?o obo:RO_0002162 ?taxon .
				?gene dcterms:identifier ?id .
				?taxon up:commonName '$$fruitfly$$' .
			}
			LIMIT 100
		}
		SERVICE <https://sparql.omabrowser.org/lode/sparql> {
			?cluster a orth:OrthologsCluster .
			?cluster orth:hasHomologousMember ?node1 .
			?cluster orth:hasHomologousMember ?node2 .
			?node2 orth:hasHomologousMember* ?protein2 .
			?node1 orth:hasHomologousMember* ?protein1 .
			?protein1 dcterms:identifier ?id .
			?protein2 rdfs:seeAlso ?OMA_LINK .
			FILTER ( ?node1 != ?node2 )
		}
	}
}
$$innerlimit$$

PREFIX up: <http://purl.uniprot.org/core/>
SELECT * {
	?species a up:Taxon .
	?species $$scientificname$$ ?name .
	?species up:rank up:Species .
}

PREFIX up: <http://purl.uniprot.org/core/>
SELECT * WHERE {
	?s a up:Taxon .
	?s $$scientificname$$ ?v .
	?s up:rank up:Species .
}

PREFIX dct: <http://purl.org/dc/terms/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX orth: <http://purl.org/net/orth#>
PREFIX up: <http://purl.uniprot.org/core/>
PREFIX sio: <http://semanticscience.org/resource/>
PREFIX obo: <http://purl.obolibrary.org/obo/>
PREFIX lscr: <http://purl.org/lscr#>
SELECT ?protein1 ?protein2 ?geneName2 ?species2 ?OMA_LINK2 ?Prot2_uniprot ?prot2_ensemblGeneId WHERE {
	?cluster a orth:OrthologsCluster .
	?cluster orth:hasHomologousMember ?node1 .
	?cluster orth:hasHomologousMember ?node2 .
	?node2 orth:hasHomologousMember* ?protein2 .
	?node1 orth:hasHomologousMember* ?protein1 .
	?protein1 a orth:Protein ;
		orth:organism/obo:RO_0002162/up:scientificName '$$Oryctolaguscuniculus$$' ;
		rdfs:label '$$APOCI$$' .
	?protein2 a orth:Protein ;
		rdfs:seeAlso ?OMA_LINK2 ;
		lscr:xrefUniprot ?Prot2_uniprot ;
		sio:SIO_010079/lscr:xrefEnsemblGene/dct:identifier ?prot2_ensemblGeneId ;
		rdfs:label ?geneName2 ;
		orth:organism/obo:RO_0002162/up:scientificName ?species2 .
	FILTER(?node1 != ?node2)
}

PREFIX lscr: <http://purl.org/lscr#>
PREFIX orth: <http://purl.org/net/orth#>
SELECT DISTINCT ?cluster ?protein2_OMA_URI ?protein2_uniprot_URI ?tax_name {
	VALUES (?protein1_uniprot_URI){
		(<http://purl.uniprot.org/uniprot/$$P68871$$>)
	}
	VALUES (?tax_name){
		("Primates")
	}
	?cluster a orth:OrthologsCluster.
	?cluster orth:hasHomologousMember* ?protein_OMA_1.
	?cluster orth:hasHomologousMember* ?protein2_OMA_URI.
	?protein_OMA_1 a orth:Protein.
	?protein2_OMA_URI a orth:Protein.
	?protein_OMA_1 lscr:xrefUniprot ?protein1_uniprot_URI.
	OPTIONAL {
		?protein2_OMA_URI lscr:xrefUniprot ?protein2_uniprot_URI.
	}
	?cluster orth:hasTaxonomicRange ?tax.
	?tax orth:taxRange ?tax_name.
}

