PREFIX : <http://nextprot.org/rdf/>
PREFIX dcterms: <http://purl.org/dc/terms/>
PREFIX ncit: <http://ncicb.nci.nih.gov/xml/owl/EVS/Thesaurus.owl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX sio: <http://semanticscience.org/resource/>

SELECT DISTINCT ?entry (str(?gen) as ?genx) ?umlsTerm WHERE {
 SERVICE <http://rdf.disgenet.org/sparql/> {
 SELECT DISTINCT ?protein ?umlsTerm WHERE {
 ?gda sio:SIO_000628 ?gene,?disease .
 ?disease a sio:SIO_010056 . # traits or phenotypes
 ?disease dcterms:title ?umlsTerm .
 filter(contains(str(?umlsTerm),"Long "))
 ?gene a ncit:C16612; sio:SIO_010078 ?protein .
 }
 }
 BIND(IRI(replace(str(?protein),"purl","www")) AS ?unipage) .
 ?entry :swissprotPage ?unipage .
 ?entry :gene / :recommendedName / rdfs:label ?gen .
} order by ?entry

PREFIX : <http://nextprot.org/rdf/>
PREFIX nextprot: <http://nextprot.org/rdf/entry/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT DISTINCT (str(?gn) as ?gene) ?pos (str(?snpac) as ?varid) (str(?orgaa) as ?orgAA) ?annot_type (str(?txt) as ?note) (str(?varaa) as ?varAA) ?freq where
{
 values ?entry {nextprot:NX_Q9BYF1 nextprot:NX_O15393} # proteins of interest (ACE2, TMPRSS2)
 ?entry :gene /:recommendedName / rdfs:label ?gn.
 ?entry :isoform ?iso .
 ?iso :swissprotDisplayed true; :variant ?var .
 ?var :start ?pos ; :original ?orgaa; :variation ?varaa .
 optional {?var :evidence / :alleleFrequency ?freq .}
 ?var :evidence / :reference ?xref .
 ?iso :positionalAnnotation ?annot .
 optional {?annot rdfs:comment ?txt .}
 ?annot a ?annot_type .
 {
 ?annot :start ?pos; :end ?pos.
 filter not exists {?annot a :Variant. }
 filter not exists {?annot a :SequenceConflict. }
 }
 union
 {
 ?annot a :DisulfideBond.
 {?annot :start ?pos. }
 union
 {?annot :end ?pos. }
 }
 } order by ?entry ?pos

PREFIX : <http://nextprot.org/rdf/>
PREFIX isoform: <http://nextprot.org/rdf/isoform/>
PREFIX nextprot_cv: <http://nextprot.org/rdf/terminology/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT DISTINCT ?iso ?spos ?epos ?annot_type (str(?txt) as ?note) where
{
 values ?iso { isoform:NX_Q99728-1 }
 values ?poi {107} # position of interest

{
 ?iso :positionalAnnotation ?statement . optional {?statement rdfs:comment ?txt .}

 ?statement a ?annot_type .
 ?statement :start ?spos; :end ?epos .
}
 union

{
 ?iso :proteoform ?pf .
 ?pf :difference ?varmut; :phenotypicVariation ?phvar .
 ?varmut :start ?spos; :end ?epos.
 ?phvar :term ?phtype; :impactedObject /:term /rdfs:label ?ioTermlab .
 ?phvar a ?annot_type .
 ?phtype :childOf nextprot_cv:ME_0000002; rdfs:label ?effect . # children of impact
 bind (concat(CONCAT(?effect," "),?ioTermlab) as ?txt) }

 filter((?spos <= ?poi) && (?epos >= ?poi)) # select annotations encompassing the position of interest
} order by ?spos

PREFIX : <http://nextprot.org/rdf/>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>

SELECT DISTINCT ?entry WHERE {
 ?entry a :Entry.
 filter not exists { ?entry :isoform / :region/rdf:type :Domain}
}

PREFIX : <http://nextprot.org/rdf/>
PREFIX nextprot_cv: <http://nextprot.org/rdf/terminology/>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>

SELECT DISTINCT ?entry WHERE {
 values ?mitoloc {nextprot_cv:SL-0173 nextprot_cv:GO_0005739} # SL and GO values for mitochondrion
 ?entry :isoform ?iso.
 ?iso :pdbMapping ?map.
 ?iso :cellularComponent ?loc .
 ?loc :term /:childOf ?mitoloc . # mitochondrial
 filter not exists { ?loc :negativeEvidence ?negev. } # No negative localization evidence
 {
 ?iso :medical / rdf:type :Disease.
 } union {
 ?iso :uniprotKeyword / :term ?kw .
 ?kw :termType "Disease"^^xsd:string
 filter (?kw != nextprot_cv:KW-0656)
 }
}

PREFIX : <http://nextprot.org/rdf/>

SELECT DISTINCT ?entry ?interactant WHERE {
 ?entry :isoform ?iso.
 ?entry :isoform/:binaryInteraction ?interaction.
 ?interaction :interactant ?interactant; :quality :GOLD.
 filter not exists { ?interactant a :Xref . }
 filter(?interactant != ?entry) # remove self-interacting proteins
}
order by ?entry

PREFIX : <http://nextprot.org/rdf/>

SELECT DISTINCT ?entry WHERE {
