 }

PREFIX : <http://nextprot.org/rdf/>
PREFIX nextprot_cv: <http://nextprot.org/rdf/terminology/>

SELECT DISTINCT ?entry WHERE {
 ?entry :isoform ?iso .
 {
 ?iso :goBiologicalProcess ?gofunc .
	?gofunc :term / :childOf nextprot_cv:GO_0006810. # GO value for transport
	filter not exists {?gofunc :negativeEvidence ?negev} # No negative function evidence
 } union {
 ?iso :uniprotKeyword / :term nextprot_cv:KW-0813. # KW for transport
 }
 {
 ?iso :uniprotKeyword / :term nextprot_cv:KW-0812. # transmembrane
 } union {
 ?iso :topology / :term nextprot_cv:CVTO_0004. # intramembrane
 }
 filter not exists { ?iso :uniprotKeyword / :term nextprot_cv:KW-0325 } # not a glycoprotein
}

PREFIX : <http://nextprot.org/rdf/>
PREFIX nextprot_cv: <http://nextprot.org/rdf/terminology/>

SELECT DISTINCT ?entry WHERE {
 select ?entry ?iso (count(?ptm) as ?ptmCnt) WHERE {
 ?entry :isoform ?iso.
 ?iso :modifiedResidue ?ptm.
 ?ptm :term nextprot_cv:PTM-0255 #phosphotyrosine.
 filter (
 not exists { ?iso :modifiedResidue / :term nextprot_cv:PTM-0253. } #phosphoserine
 &&
 not exists { ?iso :modifiedResidue / :term nextprot_cv:PTM-0254. } #phosphothreonine
 )
 } group by ?entry ?iso having ( count(?ptm) >= 1 )
}

PREFIX : <http://nextprot.org/rdf/>
PREFIX db: <http://nextprot.org/rdf/db/>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>

SELECT DISTINCT ?entry ?publications WHERE {
 values ?pmid {"20570859"^^xsd:string "14760718"^^xsd:string}
 # get all assertions from the publications
 ?entry :isoform/:glycosylationSite ?statement.
 ?statement :evidence/:reference/:from ?xref .
 ?xref :accession ?pmid ; :provenance db:PubMed .
}

PREFIX : <http://nextprot.org/rdf/>

SELECT DISTINCT ?entry WHERE {
 ?entry :isoform / :sequence / :chain ?chain1.
 ?entry2 :isoform / :sequence / :chain ?chain2.
 filter ( (?chain1 = ?chain2) && (?entry != ?entry2))
}

PREFIX : <http://nextprot.org/rdf/>
PREFIX nextprot_cv: <http://nextprot.org/rdf/terminology/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

#A0A1B0GVQ0,A0AVT1 ,A0PJK1...



SELECT DISTINCT ?entry WHERE {
 ?entry :isoform ?iso1, ?iso2.
 ?iso1 :swissprotDisplayed true .
 ?iso2 :swissprotDisplayed false .
 {
 ?iso2 :function / :term ?functerm .
