PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX rh: <http://rdf.rhea-db.org/>

# Query 4
# Select all cross-references mapped to RHEA:21016
# 
# This query mimics the Cross-references section of 
# https://www.rhea-db.org/rhea/21016
#
SELECT distinct ?rhea ?rheaDir ?xref 
WHERE {
  ?rhea rdfs:subClassOf rh:Reaction .
  VALUES (?rhea) {(rh:21016)}
  {
    ?rhea rdfs:seeAlso ?xref .
    BIND(?rhea as ?rheaDir)
  }
  UNION
  {
    ?rhea rh:directionalReaction ?directionalReaction .
    ?directionalReaction rdfs:seeAlso ?xref . 
    BIND(?directionalReaction as ?rheaDir  )
  }
  UNION
  {
    ?rhea rh:bidirectionalReaction ?bidirectionalReaction .
    ?bidirectionalReaction rdfs:seeAlso ?xref . 
    BIND(?bidirectionalReaction as ?rheaDir  )
  }
}

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX rh: <http://rdf.rhea-db.org/>
PREFIX sachem: <http://bioinfo.uochb.cas.cz/rdf/v1.0/sachem#>


SELECT 
  ?rhea 
  ?chebi
WHERE {
  SERVICE <https://idsm.elixir-czech.cz/sparql/endpoint/chebi> {
    ?chebi sachem:substructureSearch [
        sachem:query "[C@]12(CCC3CCCC[C@]3(C)[C@@]1([H])CC[C@]1(C)[C@@]([H])([C@@](C)([H])CCCC(C)C)CC[C@@]21[H])[H]" ].
  }
  ?rhea rh:side/rh:contains/rh:compound/rdfs:subClassOf ?chebi .
}

PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX sp: <http://spinrdf.org/sp#>
PREFIX up: <http://purl.uniprot.org/core/>


SELECT ?rule ?enzymeClass
WHERE
{
 ?rule a sp:Construct ;
     sp:templates/rdf:rest*/rdf:first ?annotationsToAdd .
 ?annotationsToAdd sp:predicate up:enzymeClass ;
                   sp:object ?enzymeClass .
}

PREFIX sh: <http://www.w3.org/ns/shacl#>

SELECT ?rule
WHERE
{
    ?rule sh:construct ?query  .
}

uniprottaxons.fetchQuery:
PREFIX up: <http://purl.uniprot.org/core/>
SELECT DISTINCT ?name ?value
WHERE
{
?value a up:Taxon ;
up:scientificName ?name
FILTER(CONTAINS(lcase(str(?name)), "$$searchString$$"))
} LIMIT 100

taxlevels.fetchQuery:
PREFIX orth: <http://purl.org/net/orth#>
SELECT DISTINCT ?value
WHERE {
?taxRange orth:taxRange ?value .
FILTER(CONTAINS(lcase(str(?value)), "$$searchString$$"))
}LIMIT 100

proteinsatoma.fetchQuery:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX orth: <http://purl.org/net/orth#>
SELECT DISTINCT ?value WHERE {
?proteinOMA a orth:Protein;
rdfs:label ?value .
FILTER(STRSTARTS(lcase(str(?value)), "$$searchString$$"))}
